import React, { useState } from 'react';
import LazyLoad from 'react-lazyload';
import potteryData from '../data/pottery';
import './PotteryGrid.css';
import '../styles/App.css';

const PotteryGrid = () => {
    const [expandedImage, setExpandedImage] = useState(null);

    const handleImageClick = (image) => {
        setExpandedImage(expandedImage === image ? null : image);
    };

    return (
        <>
            <div className="pottery-grid">
                {potteryData.map(item => (
                    <div 
                        key={item.id}
                        className={`pottery-item ${expandedImage === item.imageUrl ? 'expanded' : ''}`}
                        onClick={() => handleImageClick(item.imageUrl)}
                    >
                        <LazyLoad height={200} offset={100} once>
                            <img src={item.imageUrl} alt={item.title} />
                        </LazyLoad>
                    </div>
                ))}
            </div>
            <div 
                className={`overlay ${expandedImage ? 'active' : ''}`}
                onClick={() => setExpandedImage(null)}
            />
        </>
    );
};

export default PotteryGrid;
